import React, {Component} from 'react';
import Node from './Node/node';
import './pathfinder.css';
import {dijkstra, getNodesInShortestPath} from '../algorithms/dijkstra';

const START_NODE_ROW = 10;
const START_NODE_COL = 15;
const FINISH_NODE_ROW = 10;
const FINISH_NODE_COL = 35;
const TOTAL_ROW = 20;
const TOTAL_COL = 50;

export default class Pathfinder extends Component
{
	constructor()
	{
		super();
		this.state = {
			grid: [],
			mouseIsPressed: false,
			isSelectingStart: true,
			isSelectingEnd: false
		};
	}

	componentDidMount() 
	{
		const grid = getInitialGrid();
		this.setState({grid});
	}

	handleMouseDown(row, col) 
	{
		const newGrid = getNewGridWithBarrierToggled(this.state.grid, row, col);
		this.setState({grid: newGrid, mouseIsPressed: true});
	}

	handleMouseEnter(row, col) 
	{
		if (!this.state.mouseIsPressed) return;
		const newGrid = getNewGridWithBarrierToggled(this.state.grid, row, col);
		this.setState({grid: newGrid});
	}

	handleMouseUp() 
	{
		this.setState({mouseIsPressed: false});
	}

	animateDijkstra(visitedNodesInOrder, nodesInShortestPathOrder) 
	{
		for (let i = 0; i <= visitedNodesInOrder.length; i++) 
		{
			if (i === visitedNodesInOrder.length) 
			{
				setTimeout(() => {
					this.animateShortestPath(nodesInShortestPathOrder);
				}, 10 * i);

				return;
			}
			setTimeout(() => {
			const node = visitedNodesInOrder[i];
			document.getElementById(`node-${node.row}-${node.col}`).className =
				'node node-visited';
			}, 10 * i);
		}
	}

	animateShortestPath(nodesInShortestPathOrder) 
	{
		for (let i = 0; i < nodesInShortestPathOrder.length; i++) 
		{
			setTimeout(() => 
			{
				const node = nodesInShortestPathOrder[i];
				document.getElementById(`node-${node.row}-${node.col}`).className =
					'node node-shortest-path';
			}, 50 * i);
		}
	}

	visualizeDijkstra() 
	{
		const {grid} = this.state;
		const startNode = grid[START_NODE_ROW][START_NODE_COL];
		const finishNode = grid[FINISH_NODE_ROW][FINISH_NODE_COL];
		const visitedNodesInOrder = dijkstra(grid, startNode, finishNode);
		const nodesInShortestPathOrder = getNodesInShortestPath(finishNode);
		this.animateDijkstra(visitedNodesInOrder, nodesInShortestPathOrder);
	}

	render() 
	{
		const {grid, mouseIsPressed} = this.state;
		return (
		<>
			<nav class="navbar navbar-inverse">
				<div class="container-fluid">
					<div class="navbar-header">
						<a class="navbar-brand" href="#">Pathfinding algorithms Visualizer</a>
					</div>
					<li class="dropdown navbar-left">
						<button class="btn dropdown-toggle" data-toggle="dropdown" >Select Algorithm<span class="caret"></span></button>
						<button class="btn dropdown-menu" onClick={() => this.visualizeDijkstra()}>Dijkstra's Algorithm</button>
						<div><button class = "btn btn-success">Select Start Node</button></div>  
					</li>
				</div>
			</nav>
			<div className="grid">
				{grid.map((row, rowIdx) => {
				return (
					<div key={rowIdx}>
					{row.map((node, nodeIdx) => {
						const {row, col, isFinish, isStart, isBarrier} = node;
						return (
						<Node
							col={col}
							isFinish={isFinish}
							isStart={isStart}
							isBarrier={isBarrier}
							key={nodeIdx}
							mouseIsPressed={mouseIsPressed}
							onMouseDown={(row, col) => this.handleMouseDown(row, col)}
							onMouseEnter={(row, col) => this.handleMouseEnter(row, col)}
							onMouseUp={() => this.handleMouseUp()}
							row={row}></Node>
						);
					})}
					</div>
				);
				})}
			</div>
		</>
	  );
	}
}

	const getInitialGrid = () => {
		const grid = [];
		for (let row = 0; row < TOTAL_ROW; row++) 
		{
			const currentRow = [];
			for (let col = 0; col < TOTAL_COL; col++) 
			{
				currentRow.push(createNode(col, row));
			}
			grid.push(currentRow);
		}

		return grid;
	};

	const createNode = (col, row) => {
		return {
		col,
		row,
		isStart: row === START_NODE_ROW && col === START_NODE_COL,
		isFinish: row === FINISH_NODE_ROW && col === FINISH_NODE_COL,
		distance: Infinity,
		isVisited: false,
		isBarrier: false,
		previousNode: null,
		};
	};

	const getNewGridWithBarrierToggled = (grid, row, col) => {
		const newGrid = grid.slice();
		const node = newGrid[row][col];
		const newNode = {
		...node,
		isBarrier: !node.isBarrier,
		};
		newGrid[row][col] = newNode;
		return newGrid;
	};
